package assignment;

public class primenumber {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		{
	int a=0;
	int n=10;
	for (int i=1;i<=1;i++)
	{
		if(n%i==0)
			a++;
	}
	if (a==2)
			{
		System.out.println("given number is prime"+n);
			}
	else
	{System.out.println("given number is not prime");

			}
		}
	}
}

	
